mapper.select:
  	SELECT s.pos_id 
	FROM sta_pos_relation s 
	WHERE s.staff_id = #{id}

  	SELECT * 
	FROM staff u 
	WHERE u.job_number = #{name}

  	SELECT * 
	FROM staff 
	WHERE job_number=#{username} AND password = #{password}

mapper.insert:
insert into staff (staff_id, shop_id, pos_id, 
  job_number, staff_name, password, 
  staff_state, create_time, trade, 
  head_portrait_path, phone, salt
  )
values (#{staffId,jdbcType=INTEGER}, #{shopId,jdbcType=SMALLINT}, #{posId,jdbcType=INTEGER}, 
  #{jobNumber,jdbcType=VARCHAR}, #{staffName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{staffState,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{trade,jdbcType=VARCHAR}, 
  #{headPortraitPath,jdbcType=VARCHAR}, #{phone,jdbcType=CHAR}, #{salt,jdbcType=VARCHAR}
  )

mapper.select:
 	 SELECT *,s.`shop_name` AS shopName,p.`pos_name` AS posName FROM staff sf,shop s,`position` p WHERE sf.`shop_id`=s.`shop_id` AND p.`pos_id`=sf.`pos_id`

		and sf.shop_id = #{shopId} 


        and  sf.pos_id = #{posId,jdbcType=INTEGER}


        and sf.job_number LIKE '%${jobNumber}%'

mapper.update:
UPDATE staff 
SET head_portrait_path=#{headPortraitPath} 
WHERE staff_id=#{staffId}
